import Immutable from 'immutable';
export default function serialize(immutable: typeof Immutable, refs?: (new (data: any) => unknown)[] | null, customReplacer?: (key: string, value: unknown, defaultReplacer: (key: string, value: unknown) => unknown) => unknown, customReviver?: (key: string, value: unknown, defaultReviver: (key: string, value: unknown) => unknown) => unknown): {
    replacer: (key: string, value: unknown) => unknown;
    reviver: (key: string, value: unknown) => unknown;
    options: {
        refs: boolean;
        date: boolean;
        function: boolean;
        regex: boolean;
        undefined: boolean;
        error: boolean;
        symbol: boolean;
        map: boolean;
        set: boolean;
        nan: boolean;
        infinity: boolean;
    };
};
